    private void BCelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BCelActionPerformed
        int Cel=Integer.parseInt(TCel.getText());
        double fah=(1.8*Cel)+32;
        double rea=(0.8*Cel);
        double kel=(Cel+273);
        
        TFah.setText(String.valueOf(fah));
        TRea.setText(String.valueOf(rea));
        TKel.setText(String.valueOf(kel));
        // TODO add your handling code here:
    }//GEN-LAST:event_BCelActionPerformed

