    private void BKelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BKelActionPerformed
        int Kel=Integer.parseInt(TKel4.getText());
        double cel=(Kel-273.15);
        double fah=(Kel-273.15)*0.8;
        double rea=(Kel*1.8)-459.67;
        
        TCel4.setText(String.valueOf(cel));
        TFah4.setText(String.valueOf(fah));
        TRea4.setText(String.valueOf(rea));
        
        // TODO add your handling code here:
    }//GEN-LAST:event_BKelActionPerformed

